import React, { useState, useEffect } from "react";
import './FindRecipe.css';
import axios from "axios";
import SearchBar from "../SearchBar/SearchBar";

function FindRecipe() {

  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isListVisible, setIsListVisible] = useState(false);
  const [buttonText, setButtonText] = useState("Show List");

  useEffect(() => {
    async function fetchRecipes() {
      setLoading(true);
      try {
        const response = await axios.get("http://localhost:9000/recipes");
        setRecipes(response.data);
        setLoading(false);
      } catch (error) {
        setError("Request failed. Please check your Internet connection and try again.");
        setLoading(false);
      }
    }

    fetchRecipes();
  }, []);

  const toggleListVisibility = () => {
    setIsListVisible(!isListVisible);
    setButtonText(isListVisible ? "Show List" : "Hide List"); // Change the button text
  };

  return (
    <div>
      <SearchBar />
      <h2>Recipe List</h2>
      <button id="toggleButton" onClick={toggleListVisibility}>
        {buttonText}
      </button>
      <div id="image" className={isListVisible ? "visible" : "hidden"}>
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : (
          recipes.map((recipe, index) => (
            <div key={index} className="recipeThumbnail">
              <img src={recipe.image} alt={recipe.name} />
              <p>{recipe.name}</p>
            </div>
          ))
        )}
      </div>
      <img src="https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Ftse1.mm.bing.net%2Fth%3Fid%3DOIP.avb9nDfw3kq7NOoP0grM4wHaEK%26pid%3DApi&f=1&ipt=9522b1e2c4315a794db580def230582f228d3bca999f2ee4b04bbbf07ba836da&ipo=images"></img>
    </div>
  );
}

export default FindRecipe;
