import React, { useState, useEffect } from "react";

function Search() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    // Function to fetch recipes
    const fetchRecipes = async () => {
      setLoading(true);
      try {
        const response = await fetch("http://localhost:9000/recipes");
        if (!response.ok) {
          throw new Error("Request failed. Please check your Internet connection and try again.");
        }
        const data = await response.json();
        setRecipes(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (searchInput) {
      fetchRecipes();
    }
  }, [searchInput]);

  return (
    <div>
      <input
        type="text"
        id="search"
        placeholder="Search for recipes"
        value={searchInput}
        onChange={(e) => setSearchInput(e.target.value)}
      />
      {loading && <div className="loader-container">Loading...</div>}
      {error && <div id="msg-top">{error}</div>}
      <ul>
        {recipes.map((recipe, index) => (
          <li key={index}>{recipe.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default Search;
