import React, { Component } from "react";

class RecipeList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: [],
      loading: false,
      error: null,
      searchInput: "",
    };
  }

  handleSearchInputChange = (event) => {
    this.setState({ searchInput: event.target.value });
  };

  handleSearch = () => {
    if (this.state.searchInput) {
      this.setState({ loading: true });
      fetch("http://localhost:9000/recipes")
        .then((response) => {
          if (!response.ok) {
            throw new Error("Request failed. Please check your Internet connection and try again.");
          }
          return response.json();
        })
        .then((data) => {
          this.setState({
            recipes: data,
            loading: false,
            error: null,
          });
        })
        .catch((error) => {
          this.setState({
            recipes: [],
            loading: false,
            error: error.message,
          });
        });
    }
  };

  render() {
    const { recipes, loading, error } = this.state;

    return (
      <div>
        <input
          type="text"
          id="search"
          placeholder="Search for recipes"
          value={this.state.searchInput}
          onChange={this.handleSearchInputChange}
        />
        {loading && <div className="loader-container">Loading...</div>}
        {error && <div id="msg-top">{error}</div>}
        <ul>
          {recipes.map((recipe, index) => (
            <li key={index}>{recipe.name}</li>
          ))}
        </ul>
      </div>
)}};
export default RecipeList;
