import React, { useState, useEffect } from "react";
import './FindRecipe.css';
import axios from "axios";

function FindRecipe() {

  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchRecipes() {
      setLoading(true);
      try {
        const response = await axios.get("http://localhost:9000/recipes");
        setRecipes(response.data);
        setLoading(false);
      } catch (error) {
        setError("Request failed. Please check your Internet connection and try again.");
        setLoading(false);
      }
    }

    fetchRecipes();
  }, []);

  return(
    <body>
<div class="Card">
  <div class="CardInner">
  <label>Search for your favourite food</label>
  <div class="container">
  <div class="Icon">
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#657789" stroke-width="3" stroke-linecap="round" stroke-linejoin="round" class="feather feather-search"><circle cx="11" cy="11" r="8"/><line x1="21" y1="21" x2="16.65" y2="16.65"/></svg>
    </div>
    <div class="InputContainer">
      <input placeholder="It just can't be pizza..."/>
    </div>
  </div>
 </div>
</div>
<div>
<h2>Recipe List</h2>
{loading ? (
  <p>Loading...</p>
) : error ? (
  <p>{error}</p>
) : (
  <ul>
    {recipes.map((recipe, index) => (
      <li key={index}>{recipe.name}</li>
    ))}
  </ul>
)}
</div>
</body>
  )
}

export default FindRecipe
