import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

function SearchBar() {
  const [searchQuery, setSearchQuery] = useState('');
  const history = useHistory();

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:9000/search?q=${searchQuery}`);
      const searchResults = response.data;

      // Navigate to the search results page and pass the results as state
      history.push('/search-results', { searchResults });
    } catch (error) {
      console.error('Error:', error);
    }
  }

  return (
    <div>
      <label>Search for your favorite food</label>
      <input
        placeholder="Favorite ingredient here"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchBar;
